import React, { useState } from 'react';
import { View, Platform, Text, KeyboardAvoidingView, TextInput } from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import { Feather as Icon } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native'

import Button from '../../../components/Button'


import {
  Container,
  Title,
  BackToHome,
  ButtonDateTime,
  ButtonDateTimeText,
} from './styles'
import { RectButton } from 'react-native-gesture-handler';

const PartyRegistrationDateTime = () => {

  function formatToHoursAndMinutes(date: Date){
    const h = date.getHours()
    const m = date.getMinutes()
    const formated = `${(h < 10) ? '0' + h : h}:${(m < 10) ? '0' + m : m}`
    return formated
  }


  function formatDate(date: Date){
    const d = date.getDay()
    const m = date.getMonth()
    const y = date.getFullYear()
    const formated = `${(d < 10) ? '0' + d : d }/${(m < 10) ? '0' + m : m }/${y}`
    return formated
  }


  const navigation = useNavigation()

  const [hour, setHour] = useState(new Date());
  const [date, setDate] = useState(new Date());
  const [mode, setMode] = useState('date');
  const [show, setShow] = useState(false);

  const onChange = (event: any, selectedDate: any): any => {
    const currentDate = selectedDate || date;
    setShow(Platform.OS === 'ios');
    setHour(currentDate)
    setDate(currentDate);
  };

  const showMode = (currentMode: string) => {
    setShow(true);
    setMode(currentMode);
  };

  const showDatepicker = () => {
    showMode('date');
  };

  const showTimepicker = () => {
    showMode('time');
  };

  return (
    <>
      <KeyboardAvoidingView
        style={{ flex: 1 }}
        behavior={Platform.OS === 'ios' ? 'padding' : undefined}
        enabled
      >
        <Container>
          <View>
            <BackToHome onPress={() => navigation.goBack()}>
              <Icon name="arrow-left" size={30} color="#34cb78" />
            </BackToHome>

            <Title>Escolha uma data e hor√°rio</Title>
          </View>

          <View style={{ width: '100%', marginTop: '30%' }}>

            <ButtonDateTime onPress={showDatepicker}>
              <Icon style={{ marginRight: '3%' }} name="calendar" color="#fff4" size={20} />
              <ButtonDateTimeText>
                {formatDate(date)}
              </ButtonDateTimeText>
            </ButtonDateTime>

            <ButtonDateTime onPress={showTimepicker}>
              <Icon style={{ marginRight: '3%' }} name="clock" color="#fff4" size={20} />
              <ButtonDateTimeText>
                {formatToHoursAndMinutes(hour)}
              </ButtonDateTimeText>
            </ButtonDateTime>

          </View>

          <View style={{ width: '100%', marginTop: '60%' }}>
            <Button onPress={()=>{}}>Cadastrar</Button>
          </View>


          {show && (
            <DateTimePicker
              locale="pt-BR"
              testID="dateTimePicker"
              value={date}
              mode={mode === 'date' ? 'date' : 'time'}
              is24Hour={true}
              display="default"
              minimumDate={new Date}
              onChange={onChange}
            />
          )}

        </Container>
      </KeyboardAvoidingView>

    </>
  );
};


export default PartyRegistrationDateTime
