import React, { useState, useEffect } from 'react';
import Constants from 'expo-constants'
import { Feather as Icon, MaterialIcons } from "@expo/vector-icons";
import { useNavigation, useRoute } from "@react-navigation/native";
import { View, TextInput, Text, StyleSheet, TouchableOpacity, ScrollView, Image, Alert } from 'react-native';
import MapView, { Marker } from 'react-native-maps'
import { SvgUri } from 'react-native-svg'
import * as Location from 'expo-location'
import generatedMapStyle from '../../utils/MapStyle'


const Home: React.FC = () => {
    const navigation = useNavigation()

    function HandleLogout() {
        navigation.navigate('SignIn')
    }
    /* tentar fazer a borda transparente para poder continuar visualizando o mapa 
       e poder fazer o drawer sem ficar movendo o mapa  
    */

    return (
        <>
            <MapView
                style={styles.map}
                customMapStyle={generatedMapStyle}
                showsCompass={false}
                initialRegion={{
                    latitude: -27.2092052,
                    longitude: -49.6401092,
                    latitudeDelta: 0.014,
                    longitudeDelta: 0.014,
                }}
            >

                <Marker
                    coordinate={{ latitude: -27.2092052, longitude: -49.6401092 }}
                    title="FESTA"
                    description="Festa 1"
                />
            </MapView>

            {/* Criação de uma coluna invisivel a esquerda para se sobressair
       ao mapa para poder puxar o drawer*/}
            <View style={styles.invisibleLeftColumn} />

            <View style={styles.searchForm}>
                <TextInput
                    style={styles.searchInput}
                    placeholder="Buscar por festas..."
                    placeholderTextColor="#999"
                    autoCapitalize="words"
                    autoCorrect={false}
                />

                <TouchableOpacity onPress={() => { }} style={styles.loadButton}>
                    <MaterialIcons name="my-location" size={20} color="#FFF" />
                </TouchableOpacity>
            </View>
        </>
    )
}

const styles = StyleSheet.create({
    map: {
        flex: 1,
    },

    avatar: {
        width: 54,
        height: 54,
        borderRadius: 4,
        borderWidth: 4,
        borderColor: '#FFF'
    },

    callout: {
        width: 260,
    },

    devName: {
        fontWeight: 'bold',
        fontSize: 16,
    },

    devBio: {
        color: '#666',
        marginTop: 5,
    },

    devTechs: {
        marginTop: 5,
    },

    searchForm: {
        position: 'absolute',
        top: 20,
        left: 20,
        right: 20,
        zIndex: 5,
        flexDirection: 'row',
    },

    searchInput: {
        flex: 1,
        height: 50,
        backgroundColor: '#FFF',
        color: '#333',
        borderRadius: 25,
        paddingHorizontal: 20,
        fontSize: 16,
        shadowColor: '#000',
        shadowOpacity: 0.2,
        shadowOffset: {
            width: 4,
            height: 4,
        },
        elevation: 2,
    },

    loadButton: {
        width: 50,
        height: 50,
        backgroundColor: '#FF565E',
        borderRadius: 25,
        justifyContent: 'center',
        alignItems: 'center',
        marginLeft: 15,
    },
    invisibleLeftColumn: {
        position: 'absolute',
        flexDirection: 'column',
        height: '100%',
        width: '6%',
        opacity: 0.0,
    },
})



export default Home
