import React, { useState } from 'react';
import { View, Platform, Text, KeyboardAvoidingView, TextInput } from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import { FontAwesome as Icon } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native'

import Button from '../../../components/Button'

import {
  Container,
  Title,
  BackToHome,
} from './styles'

const PartyRegistrationDateTime = () => {

  const navigation = useNavigation()

  const [hour, setHour] = useState(new Date());
  const [date, setDate] = useState(new Date());
  const [mode, setMode] = useState('date');
  const [show, setShow] = useState(false);

  const onChange = (event: any, selectedDate: any): any => {
    const currentDate = selectedDate || date;
    setShow(Platform.OS === 'ios');
    setHour(currentDate)
    setDate(currentDate);
  };

  const showMode = (currentMode: string) => {
    setShow(true);
    setMode(currentMode);
  };

  const showDatepicker = () => {
    showMode('date');
  };

  const showTimepicker = () => {
    showMode('time');
  };

  return (
    <>
      <KeyboardAvoidingView
        behavior={Platform.OS === 'ios' ? 'padding' : undefined}
        enabled
      >
        <Container>

          <BackToHome onPress={() => navigation.goBack()}>
            <Icon name="arrow-left" size={30} color="#34cb78" />
          </BackToHome>
          <View>
            <Title>Escolha um ponto no mapa</Title>
          </View>

          <Button onPress={showDatepicker}>Show date picker!</Button>
          {<Text style={{ color: '#fff' }}> {hour.getTime()}</Text>}


          <Button onPress={showTimepicker}>{date.getHours().toString()}</Button>
          {<Text style={{ color: '#fff' }}> Horas: {date.getHours()} Minutos: {date.getMinutes()}</Text>}


          {show && (
            <DateTimePicker
              locale="pt-BR"
              testID="dateTimePicker"
              value={date}
              mode={mode === 'date' ? 'date' : 'time'}
              is24Hour={true}
              display="default"
              minimumDate={new Date}
              onChange={onChange}
            />
          )}

        </Container>
      </KeyboardAvoidingView>

    </>
  );
};


export default PartyRegistrationDateTime
