import React from 'react'
import { View, StyleSheet } from 'react-native'
import {
  Avatar,
  Title,
  Caption,
  Paragraph,
  Drawer,
  Text,
  TouchableRipple,
  Switch,
} from 'react-native-paper'
import { DrawerContentScrollView, DrawerItem, DrawerContentOptions } from '@react-navigation/drawer'
import { MaterialCommunityIcons as Icon, MaterialIcons  } from "@expo/vector-icons";


interface Types {
  navigation: any,
}

const DrawerContent: React.FC<Types> = ({ navigation }) => {
  return (
    <View style={{ flex: 1, backgroundColor: '#2A3C44' }}>
      <DrawerContentScrollView>
        <View style={styles.drawerContent}>
          <View style={styles.userInfoSection}>
            <View style={{ flexDirection: 'row', marginTop: 15 }}>
              <Avatar.Image
                source={{
                  uri: 'https://fhox.com.br/wp-content/uploads/2019/02/ia_fotos_0006_screenshot_21-498x402.jpg'
                }}
                size={50}
              />
              <View style={{ marginLeft: 15, flexDirection: 'column' }}>
                <Title style={styles.title}> Mariana Silva </Title>
                <Caption style={styles.caption}>@MarianaSilva</Caption>
              </View>
            </View>
          </View>
          <Drawer.Section style={styles.drawerSection}>
            <DrawerItem icon={({color, size}) => (
              <Icon name='home-outline' color={'#96A7AF'} size={size} />
            )}
              label="Home"
              labelStyle={{color:'#96A7AF'}}
              onPress={() => { navigation.navigate('Home') }}
            />
             <DrawerItem icon={({color, size}) => (
              <Icon name='heart-outline' color={'#96A7AF'} size={size} />
            )}
              label="Favoritos"
              labelStyle={{color:'#96A7AF'}}
              onPress={() => { navigation.navigate('Favoritos') }}
            />
             <DrawerItem icon={({color, size}) => (
              <Icon name='map-marker-plus' color={'#96A7AF'} size={size} />
            )}
              label="Cadastrar festas"
              labelStyle={{color:'#96A7AF'}}
              onPress={() => { navigation.navigate('PartyRegistration') }}
            />

             <DrawerItem icon={({color, size}) => (
              <MaterialIcons name='event-available' color={'#96A7AF'} size={size} />
            )}
              label="Minhas Festas"
              labelStyle={{color:'#96A7AF'}}
              onPress={() => { navigation.navigate('Dashboard') }}
            />
             <DrawerItem icon={({color, size}) => (
              <Icon name='settings' color={'#96A7AF'} size={size} />
            )}
              label="Configurações"
              labelStyle={{color:'#96A7AF'}}
              onPress={() => { navigation.navigate('Dashboard') }}
            />
          </Drawer.Section>
        </View>

      </DrawerContentScrollView>
      <Drawer.Section style={styles.bottomDrawerSection}>
        <DrawerItem icon={({ color, size }) => (
          <Icon name='exit-to-app' color={'#3DD598'} size={size} />
        )}
          label="Sair"
          labelStyle={{color:'#3DD598'}}
          onPress={() => {  }}
        />
      </Drawer.Section>
    </View>
  )
}

const styles = StyleSheet.create({
  drawerContent: {
    flex: 1,
  },
  userInfoSection: {
    paddingLeft: 20,
  },
  title: {
    fontSize: 16,
    marginTop: 3,
    fontWeight: 'bold',
    color: '#fff',
  },
  caption: {
    fontSize: 14,
    lineHeight: 14,
    color: '#fff'
  },
  row: {
    marginTop: 20,
    flexDirection: 'row',
    alignItems: 'center',
  },
  section: {
    flexDirection: 'row',
    alignItems: 'center',
    marginRight: 15,
  },
  paragraph: {
    fontWeight: 'bold',
    marginRight: 3,
  },
  drawerSection: {
    marginTop: 15,
  },
  bottomDrawerSection: {
    marginBottom: 15,

    borderTopWidth: 1,
    backgroundColor: '#286053'

  },
  preference: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingVertical: 12,
    paddingHorizontal: 16,
  },
});


export default DrawerContent
